import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
// https://hn.algolia.com/api/v1/search?query=react
const NewHacker = () => {
    const [hits, setHits] = useState([]);
    const [query, setQuery] = useState("React");
    const [loading, setLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState("");
    const handleFetchData = useRef({});
    handleFetchData.current = async () => {
        try {
            setLoading(true);
            const response = await axios.get(
                `https://n.algolia.com/api/v1/search?query=${query}`
            );
            // console.log(response);
            setHits(response.data?.hits || []);
            setLoading(false);
        } catch (error) {
            setLoading(false);
            setErrorMessage(`the err happend ${error}`);
        }
    };
    useEffect(() => {
        handleFetchData.current();
    }, [query]);
    return (
        <div>
            <input
                type="text"
                className="border border-green-500 text-black p-5 mb-5 "
                value={query}
                onChange={(e) => setQuery(e.target.value)}
            />
            {loading && (
                <div className="loading w-8 h-8 border-4 border-r-4 border-r-transparent border-blue-500 rounded-full animate-spin"></div>
            )}
            {!loading && errorMessage && <p>{errorMessage}</p>}
            {!loading &&
                hits.length > 0 &&
                hits.map((item, index) => (
                    <h3 key={item.title}>{item.title}</h3>
                ))}
        </div>
    );
};

export default NewHacker;
