import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import lodash from "lodash";
// https://hn.algolia.com/api/v1/search?query=react
const NewHacker = () => {
    const [hits, setHits] = useState([]);
    const [query, setQuery] = useState("");
    const [loading, setLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState("");
    const handleFetchData = useRef({});
    handleFetchData.current = async () => {
        try {
            setLoading(true);
            const response = await axios.get(
                `https:/hn.algolia.com/api/v1/search?query=${query}`
            );
            // console.log(response);
            setHits(response.data?.hits || []);
            setLoading(false);
        } catch (error) {
            setLoading(false);
            setErrorMessage(`the err happend ${error}`);
        }
    };
    const handleUpdateQuery = lodash.debounce((e) => {
        setQuery(e.target.value);
    }, 800);
    useEffect(() => {
        handleFetchData.current();
    }, [query]);
    return (
        <div className="bg-white mx-auto mt-5 p-5 rounded-lg shadow-md w-2/4">
            <input
                type="text"
                className="border border-gray-200 block w-full rounded-md p-5 mb-5 translate-all focus:border-blue-400 "
                placeholder="Typing your keyword ...."
                defaultValue={query}
                onChange={handleUpdateQuery}
            />
            {loading && (
                <div className="loading w-8 h-8 border-4 border-r-4 border-r-transparent border-blue-500 rounded-full animate-spin mx-auto my-10"></div>
            )}
            {!loading && errorMessage && <p>{errorMessage}</p>}
            <div className="flex flex-wrap gap-5">
                {!loading &&
                    hits.length > 0 &&
                    hits.map((item, index) => (
                        <h3 key={item.title} className="p-3 bg-gray-100">
                            {item.title}
                        </h3>
                    ))}
            </div>
        </div>
    );
};

export default NewHacker;
